import pytest
from cometa.witness_set.plutus_v2_script_set import PlutusV2ScriptSet
from cometa.scripts.plutus_scripts.plutus_v2_script import PlutusV2Script
from cometa.cbor.cbor_reader import CborReader
from cometa.cbor.cbor_writer import CborWriter
from cometa.errors import CardanoError


PLUTUS_V2_SCRIPT1_CBOR = ""
PLUTUS_V2_SCRIPT2_CBOR = ""
PLUTUS_V2_SCRIPT3_CBOR = ""
PLUTUS_V2_SCRIPT4_CBOR = ""


def create_default_plutus_v2_script(cbor: str) -> PlutusV2Script:
    """
    Creates a PlutusV2Script from CBOR hex string.
    """
    reader = CborReader.from_hex(cbor)
    return PlutusV2Script.from_cbor(reader)


def test_plutus_v2_script_set_new_creates_instance():
    """
    Test that PlutusV2ScriptSet can be created.
    """
    script_set = PlutusV2ScriptSet()
    assert script_set is not None
    assert len(script_set) == 0


def test_plutus_v2_script_set_new_with_context_manager():
    """
    Test that PlutusV2ScriptSet can be used with context manager.
    """
    with PlutusV2ScriptSet() as script_set:
        assert script_set is not None
        assert len(script_set) == 0


def test_plutus_v2_script_set_repr():
    """
    Test that PlutusV2ScriptSet has a proper string representation.
    """
    script_set = PlutusV2ScriptSet()
    assert repr(script_set) == "PlutusV2ScriptSet(len=0)"
    script_set.add(create_default_plutus_v2_script(PLUTUS_V2_SCRIPT1_CBOR))
    assert repr(script_set) == "PlutusV2ScriptSet(len=1)"


def test_plutus_v2_script_set_from_cbor_creates_instance():
    """
    Test that PlutusV2ScriptSet can be deserialized from CBOR with tag.
    """
    scripts = [
        create_default_plutus_v2_script(PLUTUS_V2_SCRIPT1_CBOR),
        create_default_plutus_v2_script(PLUTUS_V2_SCRIPT2_CBOR),
        create_default_plutus_v2_script(PLUTUS_V2_SCRIPT3_CBOR),
        create_default_plutus_v2_script(PLUTUS_V2_SCRIPT4_CBOR),
    ]
    script_set = PlutusV2ScriptSet.from_list(scripts)
    writer = CborWriter()
    script_set.to_cbor(writer)
    cbor_hex = writer.encode().hex()

    reader = CborReader.from_hex(cbor_hex)
    deserialized_set = PlutusV2ScriptSet.from_cbor(reader)
    assert deserialized_set is not None
    assert len(deserialized_set) == 4
    assert deserialized_set.use_tag


def test_plutus_v2_script_set_from_cbor_without_tag():
    """
    Test that PlutusV2ScriptSet can be deserialized from CBOR without tag.
    """
    scripts = [
        create_default_plutus_v2_script(PLUTUS_V2_SCRIPT1_CBOR),
        create_default_plutus_v2_script(PLUTUS_V2_SCRIPT2_CBOR),
        create_default_plutus_v2_script(PLUTUS_V2_SCRIPT3_CBOR),
        create_default_plutus_v2_script(PLUTUS_V2_SCRIPT4_CBOR),
    ]
    script_set = PlutusV2ScriptSet.from_list(scripts)
    script_set.use_tag = False
    writer = CborWriter()
    script_set.to_cbor(writer)
    cbor_hex = writer.encode().hex()

    reader = CborReader.from_hex(cbor_hex)
    deserialized_set = PlutusV2ScriptSet.from_cbor(reader)
    assert deserialized_set is not None
    assert len(deserialized_set) == 4
    assert not deserialized_set.use_tag


