def test_plutus_v2_script_set_to_cbor_serializes_empty_set():
    """
    Test that an empty PlutusV2ScriptSet serializes correctly.
    """
    script_set = PlutusV2ScriptSet()
    writer = CborWriter()
    script_set.to_cbor(writer)
    assert writer.encode().hex() == "d9010280"


def test_plutus_v2_script_set_to_cbor_round_trip():
    """
    Test that serializing and deserializing preserves data.
    """
    scripts = [
        create_default_plutus_v2_script(PLUTUS_V2_SCRIPT1_CBOR),
        create_default_plutus_v2_script(PLUTUS_V2_SCRIPT2_CBOR),
    ]
    original_set = PlutusV2ScriptSet.from_list(scripts)

    writer = CborWriter()
    original_set.to_cbor(writer)
    cbor_data = writer.encode()

    reader = CborReader.from_hex(cbor_data.hex())
    restored_set = PlutusV2ScriptSet.from_cbor(reader)

    assert len(restored_set) == len(original_set)


